import React from "react";
import { Link } from "react-router-dom";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import Typography from "@material-ui/core/Typography";
import Button from "@material-ui/core/Button";
import Menu from "@material-ui/core/Menu";
import useMediaQuery from "@material-ui/core/useMediaQuery";
import {ArrowDropDown} from '@material-ui/icons';

// HEADER MENU
import HeaderMenu from "./Header/HeaderMenu";

const loginBtn = () =>{
  console.log('Login btn clicked')
}
const streamBtn = () =>{
  console.log('Stream btn clicked')
}

const RenderButtons = () => {
  return (
    <>
      <Button color="inherit" onClick={streamBtn}>Streams</Button>
      <Button color="inherit" onClick={loginBtn}>Login with Google</Button>
    </>
  );
};

const RenderMenu = () => {
  const [anchorEl, setAnchorEl] = React.useState(null);

  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  return (
    <>
      <Button
        aria-controls="simple-menu"
        aria-haspopup="true"
        onClick={handleClick}
      >
        <ArrowDropDown style = {{color: '#fff'}}/>
      </Button>
      <Menu
        id="simple-menu"
        anchorEl={anchorEl}
        keepMounted
        open={Boolean(anchorEl)}
        onClose={handleClose}
      >
      <Button color="inherit" onClick={()=>{streamBtn();handleClose()}}>Streams</Button>
      <br />
      <Button color="inherit" onClick={()=>{loginBtn();handleClose()}}>Login with Google</Button>
      </Menu>
    </>
  );
};

const Header = () => {
  const matches = useMediaQuery("(max-width:450px)");
  return (
    <div>
      <AppBar
        position="static"
        style={{ backgroundColor: "#00adb5", width: "auto", height:'100%' }}
      >
        <Toolbar>
          <HeaderMenu />
          <Typography
            component={Link}
            to="/"
            variant="h6"
            className="header_title"
          >
            StreamShow
          </Typography>
          {!matches ? <RenderButtons />: <RenderMenu />}
        </Toolbar>
      </AppBar>
    </div>
  );
};
export default Header;
