import React from 'react'
import {connect} from 'react-redux'
import {fetchStreams, sucSnack} from '../../actions'
import CircleLoader from "react-spinners/CircleLoader";
import LoginForm from '../Form/LoginForm'

class StreamHome extends React.Component {
    state={
        load: null
    }
    componentDidMount(){
        this.setState({load:true})
        this.props.fetchStreams()
        setTimeout(()=>{
            this.setState({load:false})
        },1500)
    }

    componentDidUpdate(){
        if(this.props.sucsnack){
            this.props.sucSnack(false)
        }
    }
    renderHome(){
        return(
            <div>
                <h4>HomePAge</h4>
            </div>
        )
    }

    render(){
        if(this.state.load){
            return(
                <div className="Loader">
                <CircleLoader color={"#66FCF1"} loading={this.state.load} size={50} />
                </div>
            )
        }
        if(this.props.SignedIn){
            return (this.renderHome())
        }
        else if(!this.props.SignedIn){
            return(
                <LoginForm />
            )
        }
    }
}

const mapStateToProps=(state)=>{
    return {
        stream: Object.values(state.stream),
        SignedIn: state.auth.isSignedIn,
        sucsnack: state.sucsnack
    }
}
export default connect(mapStateToProps, {fetchStreams,sucSnack})(StreamHome)
