import React from 'react'
import {Field, reduxForm} from 'redux-form'
import {connect} from 'react-redux'
import {createStream} from '../../actions'
import Snackbar from '@material-ui/core/Snackbar';
import MuiAlert from '@material-ui/lab/Alert';
import CircleLoader from "react-spinners/CircleLoader";

function Alert(props) {
    return <MuiAlert elevation={6} variant="filled" {...props} />;
}
class StreamCreate extends React.Component{
    state={
        open:false,
        load: null
    }
    componentDidMount(){
        this.setState({load:true})
        setTimeout(()=>{
            this.setState({load:false})
        },2500)
    }
    // FOR SNACKBARS
    handleClose = (event, reason) => {
        if (reason === 'clickaway') {
          return;
        }
        this.setState({
            open:false
        })
    };

    renderError({error, touched}){
        if(touched && error){
            return(
                <div className="error-message">
                    {error}
                </div>
            )
        }
    }
    renderInput=({input, label, meta})=>{

        return (
            <>
                <label className="fieldLabel">{label}</label>
                <br />
                <input autoComplete="off" className="text-field" placeholder={label} {...input}/>
                {this.renderError(meta)}
            </>
        )
    }

    onSubmit =(formValues)=>{
        this.props.createStream(formValues)
        this.props.sucsnack()
    }
    
    render(){
    if(this.state.load){
        return(
            <div className="Loader">
            <CircleLoader color={"#66FCF1"} loading={this.state.load} size={50} />
            </div>
        )
    }
    else{
            return(
        <div className="createStream">
            <div className="createForm">
            <h4 id="createLabel">Create Stream</h4>
            <form onSubmit={this.props.handleSubmit(this.onSubmit)} >
                <div className="field">
                <Field  name="title" component ={this.renderInput} label="Enter Title"/>
                <Field name="description" component={this.renderInput} label="Enter Description"/>
                <button  className="submit_btn">Submit</button>
                </div >
            </form>
            <Snackbar open={this.state.open} autoHideDuration={2000} onClose={this.handleClose}>
                <Alert onClose={this.handleClose} severity="success">
                    Stream Created
                </Alert>
            </Snackbar>
        </div>
        </div>
    )
    }
    }
}

const validate = (formValues) =>{
    const errors={}

    if(!formValues.title){
        errors.title='You must enter a title'
    }
    if(!formValues.description){
        errors.description="You must enter a Description"
    }
    return errors
}

const mapStateToProps=(state)=>{
    console.log(state.sucsnack)
}

const formWrapped = reduxForm({
    form: 'streamCreate',
    validate,
    sucsnack: 'sucSnack'
})(StreamCreate)

export default connect(mapStateToProps, {createStream})(formWrapped)
