import './form.css'
import React from 'react'
import DropDown from './DropDown'
import {NavLink} from 'react-router-dom'
import {Field, reduxForm} from 'redux-form'
class LoginForm extends React.Component{
    state={
        gender: ""
    }    
    changeGender=(gender)=>{
        this.setState({gender:gender})
    }

    renderInput({input, label, meta}){
        return(
            <>
                <label className="formLabel">{label}</label>
                <input className="formField" placeholder={label} {...input}/>
            </>
        )
    }
    render(){
        return(
            <div className="formDiv">
                <form action="" className="form">
                    <h3 className="formHeader">Sign Up</h3>
                    <Field name="fullname" component ={this.renderInput} label="Full Name"/>

                    <Field name="username" component ={this.renderInput} label="Username"/>

                    <Field name="email" component={this.renderInput} label="Email"/>

                    <Field name="password" component ={this.renderInput} label="Password"/>

                    <Field name="cpassword" component ={this.renderInput} label="Confirm Password"/>
                    <DropDown changeGender={this.changeGender}/>
                    <NavLink exact to="/streams/login">Already a Member? Log In instead</NavLink>
                </form>
            </div>
        )

    }
    

}

export default reduxForm({
    form: 'loginForm',
})(LoginForm)
