import {PSIGN_IN, PSIGN_OUT, SIGN_UP} from '../actions/types'
const INITIAL_STATE = {
    isSignedIn: null,
    userId: null,
}

const authReducer = (state = INITIAL_STATE, action) =>{
    switch(action.type){
        case SIGN_UP:
            return {...state, isSignedIn: false, userId: "123456"}
        case PSIGN_IN:
            return{...state, isSignedIn:true, user:"123456"}
        case PSIGN_OUT:
            return {...state, isSignedIn: false, userId: null}
        default:
            return state
    }
}
export default authReducer
