import React  from 'react'
import Button from "@material-ui/core/Button";
class GoogleAuth extends React.Component{ 
    state={ isSignedIn: null}
    componentDidMount(){
        window.gapi.load('client:auth2', ()=>{
            window.gapi.client.init({
                clientId: '946483357011-82k5gpb45tqs5b76d8k6n9ctnmdsvo7b.apps.googleusercontent.com',
                scope: 'email'
            }).then(()=>{
                this.auth = window.gapi.auth2.getAuthInstance()
                this.setState({isSignedIn: this.auth.isSignedIn.get()})
                this.auth.isSignedIn.listen(this.onAuthChange)
            })
        })
    }
    onAuthChange = () =>{
        this.setState({isSignedIn: this.auth.isSignedIn.get()})
    }

    // TO SIGN IN
    onSignIn = () =>{
        this.auth.signIn()
    }

    // TO SIGN OUT
    onSignOut = () =>{
        this.auth.signOut()
    }

   renderAuthButton(){
        if(this.state.isSignedIn === null){
            return null
            
        }
        else if(this.state.isSignedIn){
            return <Button className="ggl-btn" color="inherit"><i className="fab fa-google icon"></i> Log Out</Button>
        }
        else{
            return <Button className="ggl-btn" color="inherit"><i className="fab fa-google icon"></i> Login</Button>
        }
    }
        render(){
            return (
                <div>
                    {this.renderAuthButton()}
                </div>
            )
        }
}

export default GoogleAuth
