import React from 'react'
import {connect} from 'react-redux'
import {fetchStreams} from '../../actions'
import Paper from '@material-ui/core/Paper';

class StreamList extends React.Component {
    componentDidMount(){
        this.props.fetchStreams()

    }

    // if user is same then show buttons
    renderAdmin(){
        if(streams.userId === this.props.currentUserId){
            return <div>Edit/Del</div>
        }
    }

    renderList(){
        return this.props.stream.map(streams =>{
            return(
                <div >
                <Paper elevation={0} />
                <Paper 
                style={{backgroundColor: '#202833', 
                color:'white', 
                marginBottom: '10px'}}
                >
                    {streams.title}
                </Paper>
                <Paper elevation={3} />
                </div>
            )
        })
    }
    render(){
        return(
        <div className="renderList">
            <h4 className="labelHead">STREAMS</h4>
            {this.renderList()}
        </div>
        )
    }
}

const mapStateToProps=(state)=>{
    return {
        stream: Object.values(state.stream),
        currentUserId: state.auth.userId
    }
}
export default connect(mapStateToProps, {fetchStreams})(StreamList)
