import streams from '../api/streams'
import server from '../api/server'
import history from '../history'

import {
    SIGN_IN, 
    SIGN_OUT, 
    CREATE_STREAM,
    FETCH_STREAMS,
    FETCH_STREAM,
    DELETE_STREAM,
    EDIT_STREAM,
    SUC_SNACK,
    SIGN_UP,
    PSIGN_IN,CHK_TKN
} from './types'

export const signIn = (userId) =>{
    return{
        type: SIGN_IN,
        payload:userId
    }
}

export const signOut = () =>{
    return{
        type: SIGN_OUT
    }
}
export const signUp=(formValues,gender)=>{
    const{name, username,email,password}=formValues
    return async(dispatch)=>{
        try{
            const response = await server.post('/register',{name, username,email,password,gender})
            dispatch({type: SIGN_UP, payload:response.data.userId})
            history.push('/login')
        }
        catch(e){
            alert(e.response.data.error)
        }

    }
}

export const psignIn = (formValues)=>{
    const{username, password}=formValues
    // return async(dispatch)=>{
    //     try{
    //         const response = await server.post('/login',{username,password})
    //         dispatch({type: PSIGN_IN, payload:response.data._id})
    //         // history.push('/')
    //     }
    //     catch(e){
    //         alert(e.response.data.error)
    //     }
    // }
    return async(dispatch)=>{
        const res = await fetch('/login',{
            method:"POST",
            headers:{
                "Content-Type":"application/json"
            },
            body: JSON.stringify({
                username,
                password
            })
        })
        const data= await res.json()
        dispatch({type:PSIGN_IN, payload:data._id})
        history.push('/home')
    }
}

export const psignOut = ()=>{
    return async(dispatch)=>{
        try{
            const res = await fetch('/logout',{
                method:"GET",
                headers:{
                    Accept: "application/json",
                    "Content-Type":"application/json"
                },
                credentials:"include"
            })
            if(res.status === 200){
                alert("User Logout")
                history.push('/home')
            }
        }
        catch(e){
            console.log(e)
        }
    }
}

export const chkToken=()=>{
    return async(dispatch)=>{
        try{
            const res = await fetch('/home',{
                method:"GET",
                headers:{
                    Accept: "application/json",
                    "Content-Type":"application/json"
                },
                credentials:"include"
            })
            if(res.status === 400){
                const error = new Error("User Not logged in")
                throw error
            }
            const data = await res.json()
            console.log(data)
            dispatch({type:CHK_TKN, payload:data._id})
        }
        catch(e){
            history.push('/')
            alert(e)
        }
    }
}

export const createStream = (formValues) =>{
    return async (dispatch, getState) =>{
        const {userId} = getState().pauth
        const response = await server.post('/streams/new',{...formValues, userId})
        console.log(response)
        dispatch({type: CREATE_STREAM, payload: response.data})
    }
}

export const fetchStreams = () =>{
    return async (dispatch) =>{
        const response = await streams.get('/streams')
        dispatch({type:FETCH_STREAMS, payload: response.data})
    }
}

export const fetchStream = (id) =>{
    return async(dispatch) =>{
        const response = await streams.get(`/streams/${id}`)
        dispatch({type: FETCH_STREAM, payload: response.data})
    }
}

export const editStream = (id, formValues) =>{
    return async(dispatch)=>{
        const response = await streams.patch(`/streams/${id}`, formValues)
        dispatch({type:EDIT_STREAM, payload:response.data})
    }
}

export const deleteStream = (id)=>{
    return async(dispatch)=>{
        await streams.delete(`/streams/${id}`)
        dispatch({type: DELETE_STREAM, payload: id})
        history.push('/')
    }
}


export const sucSnack = (status) =>{
    return{
        type: SUC_SNACK,
        payload: status
    }
}
