import React, {useEffect, useState} from 'react'
import Button from "@material-ui/core/Button";
const GoogleAuth = ()=>{ 
    const [signState, setState] = useState(null)
    useEffect(()=>{
        window.gapi.load('client:auth2', ()=>{
            window.gapi.client.init({
                clientId: '946483357011-82k5gpb45tqs5b76d8k6n9ctnmdsvo7b.apps.googleusercontent.com',
                scope: 'email'
            }).then(()=>{
                const auth = window.gapi.auth2.getAuthInstance()
                setState({isSignedIn: auth.isSignedIn.get()})
            })
        })
    },[])

        return (
            <div>
                 <Button color="inherit">Login with Google</Button>
            </div>
        )
}

export default GoogleAuth
